<template>
<div class="talk_area">
    <ul class="talk_fst">
        <li class="fst_li">
            <p v-if=scores>{{scores.overall_score.toFixed(1)}}</p>
            <p>综合评价</p>
            <p v-if=scores>高于周边商家{{scores.compare_rating | toInt}}%</p>
        </li>
        <li class="snd_li">
            <div>
                <span>服务态度</span> <img src="data:image/svg+xml;base64,">
                <span style="color:#f60" v-if=scores>{{scores.service_score.toFixed(1)}}</span>
            </div>
            <div>
                <span>菜品评价</span> <img src="data:image/svg+xml;base64,">
                <span style="color:#f60" v-if=scores>{{scores.food_score.toFixed(1)}}</span>
            </div>
            <div v-if=scores><span>送达时间</span> &nbsp;{{scores.deliver_time}}分钟</div>
        </li>
    </ul>
    <btn></btn>
    
</div>
</template>
<script>
import axios from 'axios';
import btn from './btn';
export default{
    name:'talk',
    components:{
        btn
    },
    data(){
        return{
            scores:null
        }
    },
    mounted(){
        axios.get('http://localhost:8080/static/scores.json')
        .then(response => {
        this.scores = response.data;
        })
        .catch(function(error){
        console.log(error);
        })
    },
    filters:{
        toInt:function(value){
            return (value*100).toFixed(1);
        }
    }
}
</script>
